targets.expr:
avg by (instance, name) (
  purefa_volume_performance_bandwidth_bytes{
    instance=~"$instance",
    name=~"(?i).*${sql_instance}.*",
    dimension="read_bytes_per_sec"
  }
)

avg by (instance, name) (
  purefa_volume_performance_bandwidth_bytes{
    instance=~"$instance",
    name=~"(?i).*${sql_instance}.*",
    dimension="write_bytes_per_sec"
  }
)

sum by (instance, name, dimension) (
  purefa_volume_performance_latency_usec{
    instance=~"$instance",
    name=~"(?i).*${sql_instance}.*",
    dimension=~"qos_rate_limit_usec_per_read_op|queue_usec_per_read_op|san_usec_per_read_op|service_usec_per_read_op"
  }
)

sum by (instance, name, dimension) (
  purefa_volume_performance_latency_usec{
    instance=~"$instance",
    name=~"(?i).*${sql_instance}.*",
    dimension=~"qos_rate_limit_usec_per_write_op|queue_usec_per_write_op|san_usec_per_write_op|service_usec_per_write_op"
  }
)
